#include<iostream>
#include<vector>
#include<unordered_map>
using namespace std;

class Solution {
public:
    vector<vector<int>> threeSum(vector<int>& nums) {
        vector<vector<int>> result;
        int n = nums.size();
        if (n < 3) return result; // Not enough elements for a triplet

        sort(nums.begin(), nums.end()); // Sort the array

        for (int i = 0; i < n - 2; ++i) {
            if (i > 0 && nums[i] == nums[i - 1]) continue; // Skip duplicates for the first element

            int left = i + 1, right = n - 1;
            while (left < right) {
                int sum = nums[i] + nums[left] + nums[right];
                if (sum < 0) {
                    ++left; // Move left pointer to increase sum
                } else if (sum > 0) {
                    --right; // Move right pointer to decrease sum
                } else {
                    result.push_back({nums[i], nums[left], nums[right]});
                    while (left < right && nums[left] == nums[left + 1]) ++left; // Skip duplicates for second element
                    while (left < right && nums[right] == nums[right - 1]) --right; // Skip duplicates for third element
                    ++left;
                    --right;
                }
            }
        }
        return result;
    }
};


